#include <iostream>
#include <cassert>
#include <vector>
#include <chrono>

class Solution {
public:
    int findMaxK(std::vector<int>& nums) {
        std::sort(nums.begin(), nums.end());
        int left = 0;
        int right = nums.size() - 1;

        while (left < right) {
            if (nums[left] < 0 && std::abs(nums[left]) == nums[right]) {
                // std::cout << "Returning " << nums[right] << "\n";
                return nums[right];
            } else if (std::abs(nums[left]) > nums[right]) {
                ++left;
            } else if (std::abs(nums[left]) < nums[right]) {
                --right;
            } else if (nums[left] > 0 && nums[right] > 0) {
                return -1;
            }
        }

        return -1;
    }
};
